mapper.update:
UPDATE users
SET password = #{newPassword}
WHERE username = #{username}

mapper.select:
SELECT id, userid, projectid, role, joinedat
FROM user_project
WHERE userid = #{userid}

SELECT id, projectname, projectadministrator
FROM projectadd
WHERE projectadministrator = #{projectadministrator}

SELECT id, projectname, projectadministrator
FROM projectadd
WHERE projectname = #{projectname}

mapper.delete:
DELETE
FROM userproject
WHERE userid = #{userid}
  AND projectid = #{projectid}

mapper.select:
SELECT id, userid, projectid, role, joinedat
FROM userproject
WHERE userid = #{userid}
  AND projectid = #{projectid}

SELECT id, projectname, projectadministrator, region, projectdescription
FROM projectadd
WHERE id = #{id}
